mapper.update:
update car_sys_user
SET
update_time=#{updateTime},
update_user=#{updateUser}

    , user_name = #{userName}


    , user_password = #{userPassword}


    , user_real_name = #{userRealName}


    , user_phone = #{userPhone}


    , user_email = #{userEmail}


    , user_address = #{userAddress}


    , user_qq = #{userQq}


    , free = #{free}


    , is_sys = #{isSys}


    , dept_id = #{deptId}


WHERE id = #{id}

UPDATE car_sys_user
SET status="0"
WHERE id=#{id}

UPDATE car_sys_user
SET

    free = #{free}

WHERE id=#{id}
